mapper.delete:
delete from SYS_ROLE


		uuid= #{uuid}


		AND role_id= #{roleid}


		AND role_name= #{rolename}


		AND data_scope= #{datascope}


		AND cte_utff_no= #{cteutffno}


		AND dt_cte= #{dtcte}


		AND ute_utff_no= #{uteutffno}


		AND dt_ute= #{dtute}


		AND remarks= #{remarks}


		AND atv_flg= #{atvflg}


		AND org_id= #{orgid}

mapper.select:
select
uuid,role_id,role_name,data_scope,cte_utff_no,dt_cte,ute_utff_no,dt_ute,remarks,atv_flg,org_id
from SYS_ROLE


		uuid= #{uuid}


		AND role_id= #{roleid}


		AND role_name= #{rolename}


		AND data_scope= #{datascope}


		AND cte_utff_no= #{cteutffno}


		AND dt_cte= #{dtcte}


		AND ute_utff_no= #{uteutffno}


		AND dt_ute= #{dtute}


		AND remarks= #{remarks}


		AND org_id= #{orgid}

	AND atv_flg not in '3'

select count(1) from SYS_ROLE


		atv_flg<>3


		uuid= #{uuid}


		AND role_id= #{roleid}


		AND role_name like #{rolename}


		AND data_scope= #{datascope}


		AND cte_utff_no= #{cteutffno}


		AND dt_cte= #{dtcte}


		AND ute_utff_no= #{uteutffno}


		AND dt_ute= #{dtute}


		AND remarks= #{remarks}


		AND atv_flg= #{atvflg}


		AND org_id= #{orgid}


select
uuid,role_id,role_name,data_scope,cte_utff_no,dt_cte,ute_utff_no,dt_ute,remarks,atv_flg,org_id
from SYS_ROLE


		atv_flg<>3


		uuid= #{uuid}


		AND role_id= #{roleid}


		AND role_name like #{rolename}


		AND data_scope= #{datascope}


		AND cte_utff_no= #{cteutffno}


		AND dt_cte= #{dtcte}


		AND ute_utff_no= #{uteutffno}


		AND dt_ute= #{dtute}


		AND remarks= #{remarks}


		AND org_id= #{orgid}

	order  by CREAT_TIME desc

select t.uuid, t.role_id, t.role_name, t.data_scope, t.cte_utff_no, t.dt_cte, t.ute_utff_no, t.dt_ute, t.remarks, t.atv_flg, t.org_id, c.user_id from SYS_ROLE t left join BAP.t_Bap_Sys_User_Role c on t.role_id=c.role_id
and c.user_id=#{userId}
t.atv_flg<>3
