mapper.update:
update UAP.SYS_EMPLOYEE


		emp_no= #{empno},


		emp_nm= #{empnm},


		emp_sts= #{empsts},


		lob_id= #{lobid},


		emp_sex= #{empsex},


		emp_birthday= #{empbirthday},


		join_date= #{joindate},


		dept_uuid= #{deptuuid},


		dept_nm= #{deptnm},


		org_uuid= #{orguuid},


		org_nm= #{orgnm},


		tel_no= #{telno},


		mb_no= #{mbno},


		mail= #{mail},


		position_name= #{positionname},


		position_id= #{positionid},


		position_id_fix= #{positionidfix},


		active_flg= #{activeflg},


		created_staff_id= #{createdstaffid},


		updated_staff_id= #{updatedstaffid},


		date_created= #{datecreated},


		last_updated= #{lastupdated},


		emp_uuid= #{empuuid},


		has_other_position= #{hasotherposition},


		is_main_position= #{ismainposition},


		login_id= #{loginid},


		login_pwd= #{loginpwd},


		emp_nm_spell= #{empnmspell},


		is_allow_login= #{isallowlogin},


		created_staff_nm= #{createdstaffnm},


		updated_staff_nm= #{updatedstaffnm},


		emp_prop= #{empprop},


		pwd_err_cunt= #{pwderrcunt}




		emp_no= #{empno}


		AND emp_nm= #{empnm}


		AND emp_sts= #{empsts}


		AND lob_id= #{lobid}


		AND emp_sex= #{empsex}


		AND emp_birthday= #{empbirthday}


		AND join_date= #{joindate}


		AND dept_uuid= #{deptuuid}


		AND dept_nm= #{deptnm}


		AND org_uuid= #{orguuid}


		AND org_nm= #{orgnm}


		AND tel_no= #{telno}


		AND mb_no= #{mbno}


		AND mail= #{mail}


		AND position_name= #{positionname}


		AND position_id= #{positionid}


		AND position_id_fix= #{positionidfix}


		AND active_flg= #{activeflg}


		AND created_staff_id= #{createdstaffid}


		AND updated_staff_id= #{updatedstaffid}


		AND date_created= #{datecreated}


		AND last_updated= #{lastupdated}


		AND emp_uuid= #{empuuid}


		AND has_other_position= #{hasotherposition}


		AND is_main_position= #{ismainposition}


		AND login_id= #{loginid}


		AND login_pwd= #{loginpwd}


		AND emp_nm_spell= #{empnmspell}


		AND is_allow_login= #{isallowlogin}


		AND created_staff_nm= #{createdstaffnm}


		AND updated_staff_nm= #{updatedstaffnm}


		AND emp_prop= #{empprop}


		AND pwd_err_cunt= #{pwderrcunt}

