mapper.select:
select
emp_no,emp_nm,emp_sts,lob_id,emp_sex,emp_birthday,join_date,dept_uuid,dept_nm,org_uuid,org_nm,tel_no,mb_no,mail,position_name,position_id,position_id_fix,active_flg,created_staff_id,updated_staff_id,date_created,last_updated,emp_uuid,has_other_position,is_main_position,login_id,login_pwd,emp_nm_spell,is_allow_login,created_staff_nm,updated_staff_nm,emp_prop,pwd_err_cunt
from UAP.SYS_EMPLOYEE

	active_flg= '1' 

		and emp_no= #{empno}


		AND emp_nm= #{empnm}


		AND emp_sts= #{empsts}


		AND lob_id= #{lobid}


		AND emp_sex= #{empsex}


		AND emp_birthday= #{empbirthday}


		AND join_date= #{joindate}


		AND dept_uuid= #{deptuuid}


		AND dept_nm= #{deptnm}


		AND org_uuid= #{orguuid}


		AND org_nm= #{orgnm}


		AND tel_no= #{telno}


		AND mb_no= #{mbno}


		AND mail= #{mail}


		AND position_name= #{positionname}


		AND position_id= #{positionid}


		AND position_id_fix= #{positionidfix}


		AND active_flg= #{activeflg}


		AND created_staff_id= #{createdstaffid}


		AND updated_staff_id= #{updatedstaffid}


		AND date_created= #{datecreated}


		AND last_updated= #{lastupdated}


		AND emp_uuid= #{empuuid}


		AND has_other_position= #{hasotherposition}


		AND is_main_position= #{ismainposition}


		AND login_id= #{loginid}


		AND login_pwd= #{loginpwd}


		AND emp_nm_spell= #{empnmspell}


		AND is_allow_login= #{isallowlogin}


		AND created_staff_nm= #{createdstaffnm}


		AND updated_staff_nm= #{updatedstaffnm}


		AND emp_prop= #{empprop}


		AND pwd_err_cunt= #{pwderrcunt}



		AND rownum<2

