mapper.select:
SELECT t.USER_ID as "userId", t.USER_NAME as "userName", t.REAL_NAME as "realName", t.SEX as "sex", t.BIRTHDAY as "birthday", t.TEL_NO as "telNo", t.MAIL as "mail", t.ID_NUMBER as "idNumber", t.DEPT_NO as "deptNo", t.USER_STS as "userSts", t.LOGIN_PWD as "loginPwd", t.LOGIN_TIME as "loginTime", t.LOGIN_IP as "loginIp", t.EMPOWER_ROLES as "empowerRoles", t.IS_ALLOW_LOGIN as "isAllowLogin", t.PWD_ERR_CUNT as "pwdErrCunt", t.LAST_UPT_PWD_TIME as "lastUptPwdTime", t.CTE_USER_NO as "cteUserNo", t.UTE_USER_NO as "uteUserNo", t.UTE_DT as "uteDt", t.UTE_TM as "uteTm", t.CTE_DT as "cteDt", t.CTE_TM as "cteTm"
FROM USER t
limit #{startRow}, #{endRow}
